mapper.delete:
DELETE
FROM PROOF_LIST
WHERE PROOT_COST = #{cash}
  AND RECEIPT = #{receipt}

mapper.update:
UPDATE PROOF_LIST
SET APPLOVALSTATUS = #{applovalStatus}
WHERE EMP_CODE = #{empCode}
  AND REQUEST_DATE = TO_DATE(#{startDate}, 'YYYY-MM-DD')
  AND PROOT_COST = #{cash}

UPDATE PROOF_LIST
SET RECEIPT=#{proofImg}
WHERE PROOT_COST = #{cashCode}
